// Use enriched rankings data (already includes all fields)
const response = $input.first().json;
const enrichedRankings = response.data.enriched_rankings;

// Return enriched data ready for Data Table
return enrichedRankings.map(record => ({json: record}));

// Format report for Slack
const report = $input.first().json.report;
const summary = $input.first().json.summary;
const anomalies = $input.first().json.anomalies || [];
const top10 = $input.first().json.top10_changes || {};

