    }
  }
];

if (top10.new_entries && top10.new_entries.length > 0) {
  let top10Text = '*ðŸ”¥ Top 10 Changes:*\n';
  top10.new_entries.forEach(e => {
    top10Text += `âœ… ${e.keyword}: entered Top 10 at #${e.current_position}\n`;
