  blocks.push({"type": "section", "text": {"type": "mrkdwn", "text": top10Text}});
}

if (anomalies.length > 0) {
  let anomalyText = '*ðŸš¨ Statistical Anomalies:*\n';
  anomalies.slice(0, 3).forEach(a => {
