    anomalyText += `${emoji} ${a.keyword}: #${a.current_position} (${a.z_score}Ïƒ deviation)\n`;
  });
  blocks.push({"type": "section", "text": {"type": "mrkdwn", "text": anomalyText}});
}

blocks.push({
  "type": "section",
  "text": {"type": "mrkdwn", "text": `\`\`\`${report.substring(0, 2000)}\`\`\``}
});

return [{json: {blocks: blocks, text: report}}];
